    videoInputRef,

    // Handlers
    handleContactSelect,
    handleSendMessage,
    handleSendFile, // Consolidated file sending
    handleEmojiSelect,
    handleMessageAction,
    toggleMediaView,
    toggleFilesView,
    handleTabChange,
    handleProfileClick,
